INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351330, 'Nelfinavir', 'Apalutamide', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351331, 'Neratinib', 'Apalutamide', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided. According to some authorities, if the CYP450 3A4 inducer cannot be avoided, the dose of neratinib should be increased to 320 mg once daily. The daily dose of neratinib should not exceed 320 mg. The previous dose of neratinib may be resumed following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351332, 'Nicardipine', 'Apalutamide', 'Major', 'Potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of calcium channel blockers, the majority of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided. If coadministration is necessary, pharmacologic response should be monitored more closely following the initiation or discontinuation of the CYP450 3A4 inducer, and the CCB dosage adjusted accordingly.', 'Metabolism', 'Concomitant use of calcium channel blockers with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351333, 'Nilotinib', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme. In healthy subjects receiving the potent inducer rifampin (600 mg once daily for 12 days), nilotinib systemic exposure (AUC) was decreased approximately 80%.', 'DDInter', 1767317475, 'Concomitant use of nilotinib with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy. If coadministration is required, a dosage increase for nilotinib may be necessary depending on patient tolerability. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of nilotinib with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
