INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351411, 'Ramelteon', 'Apalutamide', 'Moderate', 'Coadministration with inducers of CYP450 isoenzymes may decrease the plasma concentrations and pharmacologic effects of ramelteon, which is metabolized by CYP450 1A2 and, to a lesser extent, by CYP450 3A4 and the 2C subfamily of isoenzymes.', 'DDInter', 1767317475, 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St. John''s wort. Other known inducers include aminoglutethimide, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, mitotane, modafinil, nafcillin, nevirapine, rifabutin, rifapentine, barbiturates and various other anticonvulsants, although the extent to which they interact with ramelteon is unknown.', 'Metabolism', 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351412, 'Ranolazine', 'Apalutamide', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The extent to which other, less potent CYP450 3A4 inducers may interact with ranolazine is unknown. Caution is advised if they are used with ranolazine.', 'Metabolism', 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
