INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351419, 'Rilpivirine', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of rilpivirine with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of rilpivirine with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
