INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351499, 'Tucatinib', 'Apalutamide', 'Major', 'Coadministration with potent CYP450 3A4 or moderate CYP450 2C8 inducers may decrease the plasma concentrations of tucatinib, which is primarily metabolized by CYP450 2C8 and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317475, 'Concomitant use of tucatinib with potent CYP450 3A4 or moderate CYP450 2C8 inducers should be avoided.', 'Metabolism', 'Concomitant use of tucatinib with potent CYP450 3A4 or moderate CYP450 2C8 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351500, 'Ubrogepant', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme. When ubrogepant was administered with the potent CYP450 3A4 inducer rifampin during in vivo studies, ubrogepant systemic exposure (AUC) decreased by 80%. Reduced efficacy of ubrogepant may occur.', 'DDInter', 1767317475, 'Concomitant use of ubrogepant with potent CYP450 3A4 inducers should be avoided.', 'Metabolism', 'Concomitant use of ubrogepant with potent CYP450 3A4 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351501, 'Upadacitinib', 'Apalutamide', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of upadacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response.', 'DDInter', 1767317475, 'The use of upadacitinib in combination with potent CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible.', 'Metabolism', 'The use of upadacitinib in combination with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351502, 'Valbenazine', 'Apalutamide', 'Major', 'Coadministration of valbenazine with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of valbenazine and its active metabolite, which are substrates of the isoenzyme. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of valbenazine.', 'DDInter', 1767317475, 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may be required whenever a strong CYP450 3A4 inducer is added to or withdrawn from therapy. Alternative treatment may be considered if an interaction is suspected.', 'Metabolism', 'The use of valbenazine with strong CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351503, 'Valdecoxib', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4, 2C9, 2C19, breast cancer resistance protein (BCRP), uridine diphosphate glucuronosyltransferase (UGT), organic anion transporting polypeptide 1B1 (OATP1B1), and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to apalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever apalutamide is added to or withdrawn from therapy.', 'Metabolism, Absorption', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
