INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351519, 'Voxelotor', 'Apalutamide', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations and pharmacologic effects of voxelotor. The proposed mechanism is accelerated clearance of voxelotor due to induction of the CYP450 3A4 isoenzyme, which is the primary route of elimination of voxelotor.', 'DDInter', 1767317475, 'The use of voxelotor with moderate to potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible. If coadministration is required, the manufacturer recommends increasing the voxelotor dose to 2500 mg once daily. Pharmacologic response to voxelotor should also be monitored more closely.', 'Metabolism', 'The use of voxelotor with moderate to potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351520, 'Warfarin', 'Apalutamide', 'Moderate', 'Apalutamide and Enzalutamide may reduce the effects of oral anticoagulants. The proposed mechanism is induction of the enzyme CYP450 2C9.', 'DDInter', 1767317475, 'Concomitant use of apalutamide or enzalutamide with warfarin and coumarin-like anticoagulants should be avoided. If concomitant therapy is required, additional INR monitoring should be conducted and anticoagulant dosage adjusted accordingly, particularly following initiation, discontinuation, or change of dosage of enzalutamide in patients who are stabilized on their anticoagulant regimen. Patients should be monitored for altered anticoagulation and advised to promptly report any signs of blood clots (e.g., chest pain, shortness of breath, and sudden loss of vision, or pain, redness or swelling in an extremity) to their physician.', 'Metabolism', 'Concomitant use of apalutamide or enzalutamide with warfarin and coumarin-like anticoagulants should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351521, 'Zaleplon', 'Apalutamide', 'Moderate', 'Coadministration with apalutamide may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4, 2C9, 2C19, breast cancer resistance protein (BCRP), uridine diphosphate glucuronosyltransferase (UGT), organic anion transporting polypeptide 1B1 (OATP1B1), and/or P-glycoprotein (P-gp) efflux transporter. The proposed mechanism involves accelerated clearance via these routes due to apalutamide-mediated induction. The resulting plasma concentrations will depend on the sensitivity of the affected drugs to these isoenzymes.', 'DDInter', 1767317475, 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever apalutamide is added to or withdrawn from therapy.', 'Metabolism, Absorption', 'Caution is advised when apalutamide is used concomitantly with drugs that are substrates of CYP450 3A4, 2C9, 2C19, BCRP, UGT, OATP1B1, and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351522, 'Zanubrutinib', 'Apalutamide', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of zanubrutinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of zanubrutinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
