INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46002, 'Bortezomib', 'Pramlintide', 'Moderate', 'Bortezomib may interfere with the therapeutic effects of insulin and other antidiabetic agents.', NULL, 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents.', NULL, 'Blood glucose should be closely monitored when bortezomib is prescribed to patients receiving insulin or other antidiabetic agents. Patients should learn to recognize the symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia. If hypo- or hyperglycemia occur during bortezomib therapy, patients should initiate appropriate remedial therapy immediately and contact their physician.', 'Others', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Ixazomib, Sotorasib, Enasidenib, More', 'Guar gum', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46072/', '[1] "Product Information. Velcade (bortezomib)." Millennium Pharmaceuticals Inc, Cambridge, MA.[2] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[3] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981): 33-6[4] "Product Information. Glucotrol (glipizide)." Pfizer US Pharmaceuticals, New York, NY.[5] "Position Statement: evidence-based nutrition principles and recommendations for the treatment and prevention of diabetes related complications. American Diabetes