INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46003, 'Bortezomib', 'Pravastatin', 'Moderate', 'Bortezomib can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Bortezomib treatment causes a peripheral neuropathy that is predominantly sensory, although cases of mixed sensorimotor neuropathy have also been reported.', NULL, 'Caution is advised if bortezomib is used with other neurotoxic agents.', NULL, 'Caution is advised if bortezomib is used with other neurotoxic agents. Patients should be closely monitored for symptoms of neuropathy such as visual disturbances or burning, tingling, pain, numbness, and/or weakness in the extremities. Patients experiencing new or worsening peripheral neuropathy may require an adjustment in the dosage and schedule of bortezomib in accordance with the product labeling.', 'Synergism', 'Gemfibrozil, Fenofibric acid, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Bempedoic acid, Colesevelam, More', 'Pertuzumab, Olaparib, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Sotorasib, Belzutifan, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46073/', '[1] "Product Information. Velcade (bortezomib)." Millennium Pharmaceuticals Inc, Cambridge, MA.[2] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6[3] Cavaletti G, Jakubowiak AJ "Peripheral neuropathy during bortezomib treatment of multiple myeloma: a review of recent studies." Leuk Lymphoma 51 (2010): 1178-87[4]