INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46017, 'Bortezomib', 'Rosuvastatin', 'Moderate', 'Bortezomib can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Bortezomib treatment causes a peripheral neuropathy that is predominantly sensory, although cases of mixed sensorimotor neuropathy have also been reported.', NULL, 'Caution is advised if bortezomib is used with other neurotoxic agents.', NULL, 'Caution is advised if bortezomib is used with other neurotoxic agents. Patients should be closely monitored for symptoms of neuropathy such as visual disturbances or burning, tingling, pain, numbness, and/or weakness in the extremities. Patients experiencing new or worsening peripheral neuropathy may require an adjustment in the dosage and schedule of bortezomib in accordance with the product labeling.', 'Synergism', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Belzutifan, Mitotane, Niraparib, Porfimer sodium, More', 'Bempedoic acid, Gemfibrozil, Fenofibric acid, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Clofibrate, Colestipol, Bempedoic acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46087/', '[1] "Product Information. Velcade (bortezomib)." Millennium Pharmaceuticals Inc, Cambridge, MA.[2] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6[3] Cavaletti G, Jakubowiak AJ "Peripheral neuropathy during bortezomib treatment of multiple myeloma: a review of recent studies." Leuk Lymphoma 51 (2010): 1178-87[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46018, 'Bortezomib', 'Rotavirus vaccine', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'Pertuzumab, Aminolevulinic acid, Tazemetostat, Tagraxofusp, Sotorasib, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Anagrelide, Ivosidenib, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46088/', '[1] F