INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46052, 'Bortezomib', 'Teriflunomide', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. The recent, concomitant, or sequential use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa.', NULL, 'Close monitoring is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immunosuppressive and hepatotoxic agents, and vice versa. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when these agents are initiated after the discontinuation of treatment with leflunomide or teriflunomide. Liver enzymes, bilirubin, platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy.', 'Synergism', 'Pertuzumab, Vismodegib, Belzutifan, Sonidegib, Mitotane, Porfimer sodium, Estramustine, Aminolevulinic acid, Tazemetostat, Vorinostat, Arsenic trioxide, More', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46122/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46053, 'Bortezomib', 'Thalidomide', 'Moderate', 'Bortezomib can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk and/or severity of nerve damage. Bortezomib treatment causes a peripheral neuropathy that is predominantly sensory, although cases of mixed sensorimotor neuropathy have also been reported.', NULL, 'Caution is advised if bortezomib is used with other neurotoxic agents.', NULL, 'Caution is advised if bortezomib is used with other neurotoxic agents. Patients should be closely monitored for symptoms of neuropathy such as visual disturbances or burning, tingling, pain, numbness, and/or weakness in the extremities. Patients experiencing new or worsening peripheral neuropathy may require an adjustment in the dosage and schedule of bortezomib in accordance with the product labeling.', 'Synergism', 'Pertuzumab, Vismodegib, Sotorasib, Venetoclax, Enasidenib, Belzutifan, Sonidegib, Mitotane, Bexarotene, Talazoparib, Tisagenlecleucel, More', 'Antithymocyte immunoglobulin (rabbit), Secukinumab, Ravulizumab, Mycophenolic acid, Abatacept, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46123/', '[1] "Product Information. Velcade (bortezomib)." Millennium Pharmaceuticals Inc, Cambridge, MA.[2] Argov Z, Mastaglia FL "Drug-induced peripheral neuropathies." Br Med J 1 (1979): 663-6[3] Cavaletti G, Jakubowiak AJ "Peripheral neuropathy during bortezomib treatment of multiple myeloma: a review of recent studies." Leuk Lymphoma 51 (2010): 1178-87[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46054, 'Bortezomib', 'Tioguanine', 'Moderate', 'The concomitant or sequential use of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with thioguanine. A high risk of liver toxicity characterized by vascular endothelial damage has been reported with long-term continuous use of thioguanine, particularly in children receiving the drug as part of maintenance therapy for acute lymphoblastic leukemia and in other conditions associated with continuous use.', NULL, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Baseline and regular monitoring of hepatic function is recommended. Thioguanine therapy should be discontinued if there is evidence of toxic hepatitis or biliary stasis, as reversal of signs and symptoms of liver toxicity have been reported upon withdrawal. Early indications of liver toxicity are signs associated with portal hypertension such as thrombocytopenia out of proportion with neutropenia and splenomegaly. Elevations of liver enzymes have also been reported, but do not always occur.', 'Synergism', 'Pertuzumab, Rucaparib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Enasidenib, Belzutifan, Mitotane, Porfimer sodium, Selinexor, More', 'Fluorouracil, Mercaptopurine, Trifluridine, Floxuridine, Pralatrexate, Decitabine, Azacitidine, Capecitabine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46124/', '[1] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Tabloid (thioguanine)." Prasco Laboratories, Cincinnati, OH.', 1767369485);
