INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46222, 'Istradefylline', 'Bosentan', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1. No data are available regarding the effects that other, less potent CYP450 3A4 inducers will have on the pharmacokinetics of istradefylline.', NULL, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inducers.', NULL, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inducers. Clinical monitoring of istradefylline should be considered whenever a moderate CYP450 3A4 inducer is added to or withdrawn from therapy. Patients should be monitored for diminished therapeutic effects.', 'Metabolism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46292/', '[1] "Product Information. Nourianz (istradefylline)." Kyowa Kirin, Inc, Bedminster, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[3] "Product Information. Nourianz (istradefylline)." Kyowa Kirin, Inc, Bedminster, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46223, 'Itraconazole', 'Bosentan', 'Moderate', 'Coadministration of bosentan with a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 may result in increased plasma concentrations of bosentan and decreased plasma concentrations of the other drug. Bosentan itself is a substrate and inducer of both CYP450 2C9 and 3A4. Theoretically, bosentan may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', NULL, 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered.', NULL, 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered. Clinical and/or laboratory monitoring may be appropriate whenever bosentan is added to or withdrawn from therapy, and the dosage of the concomitant drug adjusted as necessary. The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Concomitant administration of bosentan with both a potent CYP450 2C9 inhibitor (e.g., fluconazole, amiodarone) and a potent CYP450 3A4 inhibitor (e.g., ketoconazole, itraconazole, ritonavir) is not recommended. Concomitant administration with combination CYP450 2C9/3A4 inhibitors (e.g., delavirdine, imatinib, miconazole, mifepristone, voriconazole) should probably be avoided also, if possible.', 'Metabolism', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Lactic acid, Methazolamide, Trichlormethiazide, Ciclopirox, Probenecid, Tioconazole, Brinzolamide, More', 'Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46293/', '[1] "Product Information. Tracleer (bosentan)." Acetelion Pharmaceuticals US, Inc, South San Francisco, CA.', 1767369485);
