INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46240, 'Bosentan', 'Levacetylmethadol', 'Major', 'Coadministration with inducers of CYP450 3A4 may increase the peak activity and/or decrease the duration of action of levomethadyl acetate, which is primarily metabolized by the isoenzyme. Induction of CYP450 3A4 also results in increased formation of the active metabolites, nor-LAAM and dinor-LAAM. High plasma levels of these metabolites may increase the risk of QT interval prolongation and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Close monitoring is recommended if levomethadyl acetate is used with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The dose or dosing interval may require adjustment if an interaction is suspected. A 12-lead ECG should be performed before initiating levomethadyl acetate therapy, 12 to 14 days after initiating therapy, and periodically thereafter. Patients should be advised to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Clindamycin, Trichlormethiazide, Ciclopirox, Probenecid, More', 'Diamorphine, Acamprosate, Varenicline, Nalmefene, Nicotine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46310/', '[1] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[2] "Product Information. Orlaam (levomethadyl acetate)" Roxanne Laboratories Inc, Columbus, OH.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46241, 'Bosentan', 'Licorice', 'Moderate', 'Licorice use has been associated with hypertension and may antagonize the effects of antihypertensive agents. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects.', NULL, 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products.', NULL, 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Antagonism', 'Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Clindamycin, Ciclopirox, Vemurafenib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46311/', '[1] Cumming AM "Metabolic effects of licorice." Br Med J 1 (1977): 906[2] Epstein MT, Espiner EA, Donald RA, Hughes H "Effect of eating liquorice on the renin-angiotensin aldosterone axis in normal subjects." Br Med J 1 (1977): 488-90[3] Stewart PM, Wallace AM, Valentino R, Burt D, Shackleton CH, Edwards CR "Mineralocorticoid activity of liquorice: 11-beta-hydroxysteroid dehydrogenase deficiency comes of age." Lancet 2 (1987): 821-4[4] Elinav E, Chajek-Shaul T "Licorice consumption causing severe hypokalemic paralysis." Mayo Clin Proc 78 (2003): 767-8[5] Rosseel M, Schoors D "Chewing gum and hypokalaemia." Lancet 341 (1993): 175[6] Clyburn EB, DiPette DJ "Hypertension induced by drugs and 