INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46305, 'Bosentan', 'Perampanel', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of perampanel, which is primarily metabolized by the isoenzyme. The interaction has been reported with known CYP450 3A4 inducers such as carbamazepine, oxcarbazepine, and phenytoin.', NULL, 'According to the manufacturer, concomitant use of perampanel with strong CYP450 3A4 inducers should be avoided.', NULL, 'According to the manufacturer, concomitant use of perampanel with strong CYP450 3A4 inducers should be avoided.', 'Metabolism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Ethotoin, Brivaracetam, Methsuximide, Ezogabine, Cenobamate, Paramethadione, Mephenytoin, Fenfluramine, Lamotrigine, Eslicarbazepine, Phensuximide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46375/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46306, 'Bosentan', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Lactic acid, Methazolamide, Amphotericin B, Clindamycin, Trichlormethiazide, Ciclopirox, Tioconazole, More', 'Regorafenib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46376/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46307, 'Bosentan', 'Phenylbutazone', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of bosentan, which is metabolized by these isoenzymes. Concomitant administration of both a CYP450 2C9 inhibitor and a CYP450 3A4 inhibitor may lead to even larger increases in plasma concentrations of bosentan.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered during coadministration with CYP450 2C9 or 3A4 inhibitors.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered during coadministration with CYP450 2C9 or 3A4 inhibitors. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Concomitant administration of bosentan with both a potent CYP450 2C9 inhibitor (e.g., fluconazole, amiodarone) and a potent CYP450 3A4 inhibitor (e.g., ketoconazole, itraconazole, ritonavir) is not recommended. Concomitant administration with dual inhibitors of CYP450 2C9 and 3A4 (e.g., delavirdine, imatinib, miconazole, voriconazole) should probably be avoided also, if possible.', 'Metabolism', 'Tamsulosin, Tinidazole, Ascorbic acid, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Amphotericin B, More', 'Fenoprofen, Ketorolac, Misoprostol, Ketoprofen, Glucosamine, Oxaprozin, Chondroitin sul