INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46343, 'Bosentan', 'Ruxolitinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', NULL, 'No dosage adjustment is recommended when ruxolitinib is coadministered with a CYP450 3A4 inducer.', NULL, 'No dosage adjustment is recommended when ruxolitinib is coadministered with a CYP450 3A4 inducer. However, patients should be closely monitored and the dose titrated based on safety and efficacy.', 'Metabolism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Sulfadiazine, Lactic acid, Methazolamide, Amphotericin B, More', 'Regorafenib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Afatinib, Dacomitinib, Tivozanib, Alectinib, Binimetinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46413/', '[1] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.[2] "Product Information. Jakafi (ruxolitinib)." Incyte Corporation, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46344, 'Bosentan', 'Sacituzumab govitecan', 'Major', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) may significantly decrease the plasma concentrations of sacituzumab govitecan and its pharmacologically active metabolite, SN-38. The proposed mechanism involves decreased exposure to SN-38, a topoisomerase I inhibitor linked to sacituzumab govitecan, which is metabolized via UGT1A1. Loss of antineoplastic efficacy may occur.', NULL, 'Due to the potential loss of antitumour activity, concomitant use of sacituzumab govitecan with UGT1A1 inducers should be avoided.', NULL, 'Due to the potential loss of antitumour activity, concomitant use of sacituzumab govitecan with UGT1A1 inducers should be avoided.', 'Metabolism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46414/', '[1] "Product Information. Trodelvy (sacituzumab govitecan)." Immunomedics, Morris Plains, NJ.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46345, 'Bosentan', 'Saquinavir', 'Moderate', 'Coadministration of a protease inhibitor (PI) in combination with bosentan may increase the plasma concentration of bosentan and decrease that of the PI, unless ritonavir is included as a pharmacokinetic booster. Both PIs and bosentan are primarily metabolized by CYP450 3A4. Because PIs are also inhibitors of CYP450 3A4, they can inhibit the metabolic clearance of bosentan. Conversely, bosentan may induce the metabolism of PIs, although the effects are probably not significant in the presence of ritonavir, which is a potent CYP450 3A4 inhibitor.', NULL, 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster.', NULL, 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster. Dose adjustment of bosentan has been recommended. In patients who are starting bosentan and have been receiving PI therapy for at least 10 days, it has been recommended to start bosentan at 62.5 mg once daily or every other day based upon individual tolerability. When starting PI therapy in patients already on bosentan, it has been recommended to discontinue bosentan for at least 36 hours prior to initiation of PI therapy. After at least 10 days following the initiation of PI therapy, bosentan may be resumed at 62.5 mg once daily or every other day based upon individual tolerability. According to some authorities, use of the fixed combination atazanavir-cobicistat with bosentan is not recommended.', 'Metabolism', 'Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Sulfadiazine, Lactic acid, Methazolamide, Trichlormethiazide, Ciclopirox, Probenecid, Tioconazole, More', 'Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46415/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[5] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[7] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[8] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[11] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] Cerner Multum, Inc. "Australian Product Information." O 0[14] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[15] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.', 1767369485);
