INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46380, 'Bosentan', 'Ticagrelor', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided. Otherwise, caution is advised, and patients should be closely monitored for diminished clinical response to ticagrelor therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Betrixaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, Streptokinase, Tinzaparin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46450/', '[1] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Weeks P, Sieg A, Vahdat K, Raissi F, Nathan S "Improved ticagrelor antiplatelet effect on discontinuation of phenytoin." Ann Pharmacother 48 (2014): 644-7[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Teng R, Mitchell P, Butler K "Effect of rifampicin on the pharmacokinetics and pharmacodynamics of ticagrelor in healthy subjects." Eur J Clin Pharmacol 69 (2013): 877-83[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46381, 'Bosentan', 'Tipranavir', 'Moderate', 'Coadministration of a protease inhibitor (PI) in combination with bosentan may increase the plasma concentration of bosentan and decrease that of the PI, unless ritonavir is included as a pharmacokinetic booster. Both PIs and bosentan are primarily metabolized by CYP450 3A4. Because PIs are also inhibitors of CYP450 3A4, they can inhibit the metabolic clearance of bosentan. Conversely, bosentan may induce the metabolism of PIs, although the effects are probably not significant in the presence of ritonavir, which is a potent CYP450 3A4 inhibitor.', NULL, 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster.', NULL, 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster. Dose adjustment of bosentan has been recommended. In patients who are starting bosentan and have been receiving PI therapy for at least 10 days, it has been recommended to start bosentan at 62.5 mg once daily or every other day based upon individual tolerability. When starting PI therapy in patients already on bosentan, it has been recommended to discontinue bosentan for at least 36 hours prior to initiation of PI therapy. After at least 10 days following the initiation of PI therapy, bosentan may be resumed at 62.5 mg once daily or every other day based upon individual tolerability. According to some authorities, use of the fixed combination atazanavir-cobicistat with bosentan is not recommended.', 'Metabolism', 'Tamsulosin, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Rosuvastatin, Lactic acid, Mitapivat, Methazolamide, Clindamycin, Trichlormethiazide, Ciclopirox, More', 'Paritaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Oseltamivir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46451/', '[1] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[3] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[4] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[5] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[6] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[7] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[8] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[11] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[12] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[13] Cerner Multum, Inc. "Australian Product Information." O 0[14] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[15] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46382, 'Bosentan', 'Tizanidine', 'Major', 'Tizanidine may potentiate the hypotensive effect of some medications secondary to its alpha-2 adrenergic activity. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', NULL, 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications.', NULL, 'A lower initial dosage and cautious dosage titration should be considered when tizanidine is initiated in patients receiving hypotensive medications. Although single doses of less than 8 mg of tizanidine have not been shown effective for spasticity in controlled clinical studies, it may be prudent to initiate treatment with 4 mg doses and gradually increase in 2 to 4 mg increments until optimum effect is achieved. The dose can be repeated at 6 to 8 hour intervals as needed, up to a maximum of three doses in 24 hours and a total daily dosage of 36 mg. However, experience with single doses exceeding 8 mg and daily doses exceeding 24 mg is limited. Close monitoring for development of hypotension is recommended.', 'Synergism', 'Tinidazole, Ascorbic acid, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Amphotericin B, Ciclopirox, Darunavir, Probenecid, Tioconazole, More', 'Carisoprodol, Orphenadrine, Chlorzoxazone, Baclofen, Chlormezanone', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46452/', '[1] "Product Information. Zanaflex (tizanidine)." Acorda Therapeutics, Hawthorne, NY.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46383, 'Bosentan', 'Tofacitinib', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response; however, the extent to which these medications interact with tofacitinib has not been established.', NULL, 'Caution is recommended if tofacitinib is to be used in combination with moderate CYP450 3A4 inducers.', NULL, 'Caution is recommended if tofacitinib is to be used in combination with moderate CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended when possible.', 'Metabolism', 'Tamsulosin, Tinidazole, Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Metronidazole, Simeprevir, Rosuvastatin, Sulfadiazine, Lactic acid, Methazolamide, More', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46453/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767369485);
