INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351620, 'Erdafitinib', 'Apixaban', 'Moderate', 'Coadministration with erdafitinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter, resulting in increased toxicity of these drugs. In vitro, erdafitinib has been shown to be an inhibitor of P-gp.', 'DDInter', 1767317475, 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided. If concomitant use is required, administer erdafitinib at least 6 hours before or after administration of P-gp substrates with narrow therapeutic ranges.', 'Distribution', 'Concomitant use of erdafitinib and P-gp substrates should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
