INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351765, 'Streptokinase', 'Apixaban', 'Major', 'Concomitant use of apixaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during apixaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis.', 'DDInter', 1767317475, 'Caution is recommended if apixaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Caution is recommended if apixaban must be used with other agents that alter hemostasis.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351766, 'Sugammadex', 'Apixaban', 'Moderate', 'Sugammadex may potentiate the effects of anticoagulants. The mechanism of interaction has not been described.', 'DDInter', 1767317475, 'Hemostasis and coagulation parameters should be closely monitored when sugammadex is administered to patients receiving therapeutic anticoagulation for a preexisting or comorbid condition; patients who have received thromboprophylaxis drugs other than heparin and LMWH; or patients who have received thromboprophylaxis with heparin or LMWH and subsequently administered 16 mg/kg of sugammadex.', 'Others', 'Hemostasis and coagulation parameters should be closely monitored when sugammadex is administered to patients receiving therapeutic anticoagulation for a preexisting or comorbid condition; patients who have received thromboprophylaxis drugs other than heparin and LMWH; or patients who have received thromboprophylaxis with heparin or LMWH and subsequently administered 16 mg/kg of sugammadex.', NULL, NULL, 'Moderate', 'pharmacodynamic');
