INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1351877, 'Cyclophosphamide', 'Bupropion', 'Moderate', 'Coadministration with drugs that may affect the metabolism of bupropion via the CYP450 2B6 isoenzyme may increase the plasma concentrations of bupropion, which is primarily metabolized by this isoenzyme. The interaction has been demonstrated with clopidogrel and ticlopidine, both are CYP450 2B6 inhibitors and is suspected to occur with cyclophosphamide and ifosfamide, both are CYP450 2B6 substrates.', 'DDInter', 1767317475, 'Caution is advised if bupropion is prescribed with a drug that may affect the metabolism of bupropion via CYP450 2B6. Pharmacologic response to bupropion should be closely monitored whenever a drug that affects CYP450 2B6 metabolism is added to or withdrawn from therapy. Based on clinical response, dose adjustments of bupropion may be necessary. Patients should be advised to contact their physician if they experience potential symptoms of bupropion toxicity such as agitation, anxiety, tremor, insomnia and seizures, or neuropsychiatric symptoms such as delusions, hallucinations, psychosis, impaired concentration, paranoia and confusion.', 'Metabolism', 'Caution is advised if bupropion is prescribed with a drug that may affect the metabolism of bupropion via CYP450 2B6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
