INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46928, 'Eliglustat', 'Brentuximab vedotin', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin.', NULL, 'Caution is advised when brentuximab is used with P-gp inhibitors.', NULL, 'Caution is advised when brentuximab is used with P-gp inhibitors. Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46998/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc, Bothell, WA.[2] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc, Bothell, WA.[3] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (46929, 'Elotuzumab', 'Brentuximab vedotin', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk. The peripheral neuropathy associated with brentuximab vedotin treatment is cumulative and primarily sensory, but cases of peripheral motor neuropathy have also been reported. Coadministration of brentuximab vedotin with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with brentuximab vedotin.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects.', NULL, 'The potential for increased risk and/or severity of peripheral neuropathy and hepatotoxicity should be considered when brentuximab vedotin is used with other drugs that are also associated with these adverse effects. Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness. Liver enzymes and bilirubin should be measured before and during treatment, especially in patients with underlying hepatic disease or marked baseline transaminase elevations. Patients experiencing new, worsening, or recurrent neuropathy or hepatotoxicity may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/46999/', '[1] "Product Information. Adcetris (brentuximab vedotin)." Seattle Genetics Inc, Bothell, WA.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365204);
