INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (47326, 'Ifosfamide', 'Brexanolone', 'Moderate', 'Centrally-acting agents such as antiemetics, sedatives, narcotics, or antihistamines may add to the neurotoxic effects of ifosfamide. Administration of ifosfamide can cause CNS toxicity, which necessitates careful monitoring of the patient. Neurologic manifestations include somnolence, confusion, hallucinations, blurred vision, psychotic behavior, extrapyramidal symptoms, urinary incontinence, seizures, and in some instances, coma. There have also been reports of peripheral neuropathy. Ifosfamide neurotoxicity may occur within a few hours to a few days after initial administration and typically resolves within 48 to 72 hours of ifosfamide discontinuation.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide.', NULL, 'Due to the potential for additive effects, centrally-acting agents should be used with caution in combination with ifosfamide. If ifosfamide-induced encephalopathy occurs, both ifosfamide and non-essential concomitant CNS agents should be discontinued. Supportive therapy should be initiated and maintained until complete resolution of neurotoxicity.', 'Synergism', 'Temozolomide, Thiotepa, Carmustine, Cyclophosphamide, Busulfan, Streptozocin, Chlorambucil, Lomustine, Dacarbazine, Mechlorethamine, Bendamustine, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/47396/', '[1] Simonian NA, Gilliam FG, Chiappa KH "Ifosfamide causes a diazepam-sensitive encephalopathy." Neurology 43 (1993): 2700-2[2] "Product Information. Ifex (ifosfamide)." Bristol-Myers Squibb, Princeton, NJ.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (47327, 'Iloperidone', 'Brexanolone', 'Moderate', 'Coadministration with central nervous system (CNS) depressants (e.g., alcohol, benzodiazepines, opioids) or antidepressants may enhance the sedative effects of brexanolone and increase the likelihood or severity of sedation-related adverse reactions. Patients treated with brexanolone are at risk of excessive sedation or sudden loss of consciousness during administration.', NULL, 'Caution is advised during concomitant use of brexanolone with CNS depressants, antidepressants, or other agents that cause sedation.', NULL, 'Caution is advised during concomitant use of brexanolone with CNS depressants, antidepressants, or other agents that cause sedation. Patients should be closely monitored for excessive sedation and sudden loss of consciousness and have continuous pulse oximetry monitoring. During the brexanolone infusion, monitor for sedative effects every 2 hours during planned, non-sleep periods, and immediately stop the infusion if there are signs or symptoms of excessive sedation. After symptoms resolve, the infusion may be resumed at the same or lower dose as clinically appropriate. If pulse oximetry reveals hypoxia, immediately stop the infusion and do not resume infusion following resolution of the hypoxia. Patients should be cautioned against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until sedative effects of brexanolone and other concomitant medications have dissipated.', 'Synergism', 'Amisulpride', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/47397/', '[1] "Product Information. Zulresso (brexanolone)." Sage Therapeutics, Inc., Cambridge, MA.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
