INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1351911, 'Efavirenz', 'Bupropion', 'Moderate', 'Coadministration with inducers of CYP450 2B6 may decrease the plasma concentrations of and therapeutic effects of bupropion. The proposed mechanism is induction of CYP450 2B6-mediated metabolism of bupropion which in vitro studies have shown is metabolized to hydroxybupropion by CYP450 2B6.', NULL, 'Pharmacologic response to bupropion should be monitored closely whenever a known CYP450 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary.', NULL, 'Pharmacologic response to bupropion should be monitored closely whenever a known CYP450 inducer is added to or withdrawn from therapy, and the bupropion dosage adjusted as necessary. When bupropion/naltrexone is used for weight loss, some authorities advise against concomitant use of efavirenz (AU, CA, US). For all other indications, increases in bupropion dosage should be guided by clinical response; however, the maximum recommended dosage of bupropion should not be exceeded.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1351912, 'Eliglustat', 'Bupropion', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs).', NULL, 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs). In the absence of a concomitant CYP450 3A4 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to IMs and EMs treated with a potent or moderate CYP450 2D6 inhibitor. Poor metabolizers are not affected by CYP450 2D6 inhibition (since they already have minimal functional levels of the isoenzyme) and may also receive the reduced dosage of eliglustat, so long as they are not treated with a CYP450 3A4 inhibitor.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
