    ) VALUES (1352062, 'Bupropion', 'Olopatadine (nasal)', 'Moderate', 'Concurrent use of olopatadine nasal spray with alcohol or other central nervous system (CNS) depressants may result in additive impairment of mental alertness and performance. Somnolence has been reported in some patients using olopatadine nasal spray in clinical trials.', NULL, 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible.', NULL, 'Concomitant use of olopatadine nasal spray with alcohol or other CNS depressants should generally be avoided if possible. In the event that they are used together, patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their doctor if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
