INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (47932, 'Cenobamate', 'Brigatinib', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided. If coadministration cannot be avoided, the dose of brigatinib should be increased in 30 mg increments as tolerated every 7 days up to a maximum of twice the brigatinib dose that was tolerated prior to initiating the moderate CYP450 3A4 inducer. After discontinuation of the moderate CYP450 3A4 inducer, the brigatinib dose tolerated prior to initiating the moderate CYP450 3A4 inducer should be resumed. The potential for diminished pharmacologic effects of brigatinib should be considered during coadministration with CYP450 3A4 inducers.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48002/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[5] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (47933, 'Ceritinib', 'Brigatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the brigatinib once daily dose should be reduced by approximately 50% (i.e., from 180 mg to 90 mg, or from 90 mg to 60 mg). Patients should be closely monitored for increased adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase. Following discontinuation of the CYP450 3A4 inhibitor, the brigatinib dose that was tolerated previously may be resumed.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48003/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767363021);
