INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48058, 'Idarubicin', 'Brigatinib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Valrubicin, Plicamycin, Bleomycin, Mitomycin, Dactinomycin', 'Dabrafenib, Acalabrutinib, Dacomitinib, Pexidartinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Cobimetinib, Ibrutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48128/', '[1] "Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc, Boulder, CO.[2] "Product Information. Idamycin (idarubicin)." Pharmacia and Upjohn, Kalamazoo, MI.[3] "Multum Information Services, Inc. Expert Review Panel"[4] "Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Incivek (telaprevir)." Vertex Pharmaceuticals, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48059, 'Idelalisib', 'Brigatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the brigatinib once daily dose should be reduced by approximately 50% (i.e., from 180 mg to 90 mg, or from 90 mg to 60 mg). Patients should be closely monitored for increased adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase. Following discontinuation of the CYP450 3A4 inhibitor, the brigatinib dose that was tolerated previously may be resumed.', 'Metabolism', 'Axitinib, Futibatinib, Regorafenib, Midostaurin, Ruxolitinib, Lenvatinib, Erdafitinib, Neratinib, Nintedanib, Vandetanib, Ibrutinib, More', 'Idelalisib, Tivozanib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48129/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[4] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cam