INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48150, 'Brigatinib', 'Nadolol', 'Moderate', 'Coadministration of brigatinib with other agents that can also slow the heart rate may potentiate the risk of severe bradycardia.', NULL, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Heart rate and blood pressure should be monitored frequently, and patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48220/', '[1] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48151, 'Brigatinib', 'Nafcillin', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', NULL, 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided. If coadministration cannot be avoided, the dose of brigatinib should be increased in 30 mg increments as tolerated every 7 days up to a maximum of twice the brigatinib dose that was tolerated prior to initiating the moderate CYP450 3A4 inducer. After discontinuation of the moderate CYP450 3A4 inducer, the brigatinib dose tolerated prior to initiating the moderate CYP450 3A4 inducer should be resumed. The potential for diminished pharmacologic effects of brigatinib should be considered during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'Sulbactam, Oxacillin, Dicloxacillin, Piperacillin, Meticillin, Carbenicillin, Bacampicillin, Ticarcillin, Mezlocillin, Tazobactam, Cloxacillin, More', 'Futibatinib, Regorafenib, Alpelisib, Lenvatinib, Trametinib, Dabrafenib, Asciminib, Vandetanib, Nintedanib, Afatinib, Dacomitinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48221/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Alunbrig (brigatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[4] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[5] "Pr