INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1352757, 'Isocarboxazid', 'Apraclonidine', 'Major', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects. Despite relative alpha-2 selectivity, theoretical concerns exist that coadministration with monoamine oxidase inhibitors (MAOIs) may increase the risk of hypertension due to potentiation of alpha-1 stimulation, which produces vasoconstriction. However, the interaction has not been reported in the medical literature.', 'DDInter', 1767317475, 'The use of apraclonidine ophthalmic solution with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with apraclonidine.', 'Synergism', 'The use of apraclonidine ophthalmic solution with MAOIs is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
