INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1352798, 'Methyldopa', 'Apraclonidine', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects such as hypotension and bradycardia. The possibility for an additive or potentiating effect on blood pressure and heart rate should be considered when used with other medications that affect these parameters, such as ophthalmic and systemic beta blockers, vasodilators, cardiac glycosides, and antihypertensive agents.', 'DDInter', 1767317475, 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs. Patients should be advised to notify their physician if they experience slow pulse, irregular heartbeat, dizziness, lightheadedness, or syncope.', 'Synergism', 'Blood pressure and pulse rate should be monitored regularly when topical alpha-2 adrenergic receptor agonists are prescribed in combination with cardiovascular drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1352799, 'Methylene blue', 'Apraclonidine', 'Major', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects. Despite relative alpha-2 selectivity, theoretical concerns exist that coadministration with monoamine oxidase inhibitors (MAOIs) may increase the risk of hypertension due to potentiation of alpha-1 stimulation, which produces vasoconstriction. However, the interaction has not been reported in the medical literature.', 'DDInter', 1767317475, 'The use of apraclonidine ophthalmic solution with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with apraclonidine.', 'Synergism', 'The use of apraclonidine ophthalmic solution with MAOIs is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
