INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353072, 'Cyclophosphamide', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of chemotherapeutic agents that are primarily metabolized by CYP450 3A4. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant. In general, the effect of aprepitant on the pharmacokinetics of CYP450 3A4 substrates is expected to be greater when the substrates are administered orally as opposed to intravenously and may be altered following prolonged administration.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials. The potential for increased systemic toxicities of these agents should be considered. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials.', NULL, NULL, 'Moderate', 'pharmacodynamic');
