INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353084, 'Dexamethasone', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of some corticosteroids. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Oral dexamethasone and methylprednisolone dosage should be reduced by approximately 50% when coadministered with the aprepitant 125 mg/80 mg regimen, and intravenous methylprednisolone dosage should be reduced by approximately 25%. The daily dosage of dexamethasone administered in clinical chemotherapy-induced nausea and vomiting studies with aprepitant was reduced to account for the drug interaction. No dosage adjustment for the corticosteroid is necessary during coadministration with aprepitant in a single 40 mg dose for prevention of postoperative nausea and vomiting. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended, as it has not been studied and the drug interaction profile may change during long-term use.', 'Metabolism', 'Oral dexamethasone and methylprednisolone dosage should be reduced by approximately 50% when coadministered with the aprepitant 125 mg/80 mg regimen, and intravenous methylprednisolone dosage should be reduced by approximately 25%.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353085, 'Diazepam', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of benzodiazepines that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4. The potential for increased pharmacologic effects of the benzodiazepine, including central nervous system and respiratory depression, should be considered. The interaction is not expected to occur with lorazepam, oxazepam or temazepam, which are primarily metabolized via glucuronidation. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353086, 'Diclofenac', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9. The mechanism is accelerated clearance due to induction of CYP450 2C9 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353087, 'Dicoumarol', 'Aprepitant', 'Moderate', 'Coadministration of aprepitant or its prodrug, fosaprepitant, during warfarin therapy may result in clinically significant decreases in the INR or prothrombin time. The likely mechanism is aprepitant induction of CYP450 2C9, the isoenzyme responsible for metabolic clearance of the biologically more active S(-) enantiomer of warfarin.', 'DDInter', 1767317475, 'In patients treated with warfarin, the INR should be closely monitored in the 2-week period, particularly at 7 to 10 days, following initiation of the 3-day regimen of aprepitant or fosaprepitant with each chemotherapy cycle or following a single 40 mg dose of aprepitant for the prevention of postoperative nausea and vomiting. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking.', 'Metabolism', 'In patients treated with warfarin, the INR should be closely monitored in the 2-week period, particularly at 7 to 10 days, following initiation of the 3-day regimen of aprepitant or fosaprepitant with each chemotherapy cycle or following a single 40 mg dose of aprepitant for the prevention of postoperative nausea and vomiting.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353088, 'Dienestrol (topical)', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
