INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353091, 'Dihydroergotamine', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353092, 'Diltiazem', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
