INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97369, 'Flurazepam', 'Theophylline', 'Minor', 'Methylxanthines may antagonize the sedative effects of benzodiazepines. Aminophylline in particular has been demonstrated to attenuate the sedative effects of diazepam. The mechanism of this interaction may be related to competitive binding to intracerebral adenosine receptors.', NULL, '-', NULL, '-', 'Antagonism', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Mepolizumab, Roflumilast, Pentoxifylline, Zafirlukast, Bromotheophylline', 'Suvorexant, Chloral hydrate, Nitrazepam, Paraldehyde, Propiomazine, Ramelteon, Meprobamate, Scopolamine, Eszopiclone, Dexmedetomidine, Lemborexant, More', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97473/', '[1] Tuncok Y, Akpinar O, Guven H, Akkoclu A "The effects of theophylline on serum alprazolam levels." Int J Clin Pharmacol Ther 32 (1994): 642-5[2] Gallen JS "Aminophylline reversal of midazolam sedation." Anesth Analg 69 (1989): 268[3] Bonfiglio MF, Dasta JF "Clinical significance of the benzodiazepine-theophylline interaction." Pharmacotherapy 11 (1991): 85-7[4] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[5] Stirt JA "Aminophylline is a diazepam antagonist." Anesth Analg 60 (1981): 767-8[6] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[8] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman'