INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97371, 'Fluvoxamine', 'Theophylline', 'Major', 'Coadministration with fluvoxamine may significantly increase the serum concentrations of theophylline and the associated risk of toxicity. The mechanism is fluvoxamine inhibition of theophylline metabolism via CYP450 1A2.', NULL, 'The use of theophylline or its salts in combination with fluvoxamine should generally be avoided.', NULL, 'The use of theophylline or its salts in combination with fluvoxamine should generally be avoided. If coadministration is required, a reduction of theophylline dosage by one-half to two-thirds should be considered. Pharmacologic response and serum levels should be closely monitored following initiation, discontinuation or change of dosage of fluvoxamine, and the theophylline dosage adjusted accordingly. Patients should be advised to contact their physician if they experience signs and symptoms suggestive of theophylline toxicity such as nausea, vomiting, diarrhea, anorexia, headache, tremor, irritability, confusion, insomnia, seizure, palpitation, and arrhythmia. Other selective serotonin reuptake inhibitors including citalopram, escitalopram, fluoxetine, paroxetine, and sertraline do not significantly inhibit CYP450 1A2 and may be safer alternatives in theophylline-treated patients.', 'Metabolism', 'Maprotiline, Vortioxetine, Milnacipran, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Amoxapine, Imipramine, Esketamine, Phenelzine, More', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Reslizumab, Dyphylline, Mepolizumab, Zafirlukast, Bromotheophylline', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97475/', '[1] Orlando