INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97401, 'Theophylline', 'Lansoprazole', 'Minor', 'Lansoprazole may cause a minor decrease in the area under the plasma concentration-time curve of theophylline and related drugs. This effect appears to be small (approximately a 13% decrease in one study) and may be related either to a reduction in absorption or to enhanced clearance caused by induction of hepatic enzymes by lansoprazole. Dosage adjustment is not believed to be necessary. However, close observation for evidence of altered methylxanthine effect is recommended.', NULL, '-', NULL, '-', 'Metabolism', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Bromotheophylline', 'Tinidazole, Levofloxacin, Dexlansoprazole, Misoprostol, Bismuth subcitrate potassium, Rifabutin, Vonoprazan, Amoxicillin', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97505/', '[1] Kokufu T, Ihara N, Sugioka N, Koyama H, Ohta T, Mori S, Nakajima K "Effects of lansoprazole on pharmacokinetics and metabolism of theophylline." Eur J Clin Pharmacol 48 (1995): 391-5[2] Granneman GR, Karol MD, Locke CS "Pharmacokinetic interaction between lansoprazole and theophylline." Ther Drug Monit 17 (1995): 460-4[3] Ko JW, Jang IJ, Shin JG, Nam SK, Shin SG, Flockhart DA "Theophylline pharmacokinetics are not altered by lansoprazole in CYP2C19 poor metabolizers." Clin Pharmacol Ther 65 (1999): 606-14[4] "Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc, Deerfield, IL.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97402, 'Theophylline', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', NULL, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Omalizumab, Tezepelumab, Mercaptopurine, Aminophylline, Benralizumab, Caffeine, Reslizumab, Dyphylline, Oxtriphylline, Mepolizumab, Roflumilast, More', 'Entrectinib, Imatinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, Bosutinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97506/', '[1] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.[2]