INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97429, 'Theophylline', 'Moricizine', 'Minor', 'Moricizine may increase the plasma clearance of theophylline by 46% to 68% and decrease theophylline half-life by 20% to 34%. Conversely, theophylline toxicity may occur when moricizine is discontinued. The mechanism is unknown, but may be related to hepatic enzyme induction. Management consists of monitoring the patient for theophylline efficacy, checking levels if indicated, and adjusting theophylline dosage accordingly. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms.', NULL, '-', NULL, '-', 'Metabolism', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, More', 'Dronedarone, Procainamide, Flecainide, Dofetilide, Bretylium, Ibutilide', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97533/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Pieniaszek HJ Jr, Davidson AF, Benedek IH "Effect of moricizine on the pharmacokinetics of single-dose theophylline in healthy subjects." Ther Drug Monit 15 (1993): 199-203[3] Siddoway LA, Schwartz SL, Barbey JT, Woosley RL "Clinical pharmacokinetics of moricizine." Am J Cardiol 65 (1990): d21-5; disc. D68-71', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97430, 'Theophylline', 'Mycophenolate mofetil', 'Minor', 'In vitro studies have shown that mycophenolic acid in concentrations up to 100 mcg/mL decreases protein binding of theophylline from 53% to 45%. The clinical significance of this interaction is unknown. Decreased protein binding theoretically could lead to increased plasma theophylline concentrations. However, clinical toxicity has not been reported. It may be appropriate to monitor more closely patients receiving theophylline or other methylxanthine derivatives with mycophenolic acid products. Patients should be advised to contact their physician if they experience potential symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', NULL, '-', NULL, '-', 'Distribution', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Pentoxifylline, Zafirlukast, Bromotheophylline', NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97534/', '[1] "Product Information. CellCept (mycophenolate mofetil)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Myfortic (mycophenolic acid)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
