INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97500, 'Theophylline', 'Sulfinpyrazone', 'Minor', 'In one study, concomitant administration of sulfinpyrazone resulted in a 22% increase in total theophylline clearance. Changes noted in the elimination of theophylline included an increase in metabolism and a decrease in renal excretion. The clinical significance of this interaction is unknown. If sulfinpyrazone and theophylline are given together, clinical monitoring of patient response and serum theophylline levels is recommended. Patients should be advised to report worsening of their respiratory symptoms to their physician.', NULL, '-', NULL, '-', 'Excretion', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Zafirlukast, Bromotheophylline', 'Lesinurad, Pegloticase, Colchicine, Rasburicase, Probenecid', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97604/', '[1] Upton RA "Pharmacokinetic interactions between theophylline and other medication (Part II)." Clin Pharmacokinet 20 (1991): 135-50[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Birkett DJ, Miners JO, Attwood J "Evidence for a dual action of sulphinpyrazone on drug metabolism in man: theophylline-sulphinpyrazone interactions." Br J Clin Pharmacol 15 (1983): 567-9', 1767369485);
