INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97506, 'Theophylline', 'Terbinafine', 'Moderate', 'The coadministration of terbinafine and theophylline may result in elevated plasma concentrations of the latter. The exact mechanism is unknown but may involve inhibition of the CYP450 1A2 enzymatic pathway, which is responsible for the metabolism of theophylline and other methylxanthines such as caffeine, the clearance of which has also been found to be reduced (21%) by terbinafine.', NULL, 'Given the narrow therapeutic index of theophylline, patients should be monitored closely during concomitant therapy with terbinafine.', NULL, 'Given the narrow therapeutic index of theophylline, patients should be monitored closely during concomitant therapy with terbinafine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever terbinafine is added to or withdrawn from therapy. Due to the long elimination half-life of terbinafine, especially following prolonged use, potential drug interactions may be observed for several months after discontinuation of terbinafine therapy. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Metabolism', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Reslizumab, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, Zafirlukast, Bromotheophylline', 'Salicylic acid, Selenium Sulfide, Miconazole, Nystatin, Ciclopirox, Griseofulvin, Econazole, Flucytosine, Tioconazole, Clotrimazole, Chlorphenesin, Griseofulvin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/