INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (97510, 'Theophylline', 'Thiabendazole', 'Moderate', 'Theophylline toxicity has been reported during concomitant administration of theophylline and thiabendazole. Decreases in clearance have been noted up to nine days after discontinuation of thiabendazole. The proposed mechanism is inhibition of hepatic metabolism by thiabendazole.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended.', NULL, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians. The theophylline dosage may require reduction or alternatives agents could be considered during thiabenzole therapy.', 'Metabolism', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Zafirlukast, Bromotheophylline', 'Salicylic acid, Selenium Sulfide, Miconazole, Nystatin, Ciclopirox, Griseofulvin, Econazole, Flucytosine, Tioconazole, Clotrimazole, Chlorphenesin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/97614/', '[1] Schneider D, Gannon R, Sweeney K, Shore E "Theophylline and antiparasitic drug interactions: a case report and study of the influence of thiabendazole and mebendazole on theophylline pharmacokinetics in adult." Chest 97 (1990): 84-7[2] Lew G, Murray WE, Lane JR, Haeger E "Theophylline-thiabendazole drug interaction." Clin Pharm 8 (1989): 225-7[3] Sugar AM, Kearns PJ, Haulk AA, Rushing JL "Possible thiaben