INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48663, 'Fenoldopam', 'Timolol (ophthalmic)', 'Moderate', 'The use of fenoldopam in combination with beta-blockers has not been studied. Theoretically, beta-blockers may potentiate the risk of severe, symptomatic hypotension by inhibiting the sympathetic reflex response to fenoldopam-induced blood pressure decreases.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible.', NULL, 'Concomitant use of fenoldopam with beta-blockers should be avoided if possible. The same precaution may be applicable to beta-blocker ophthalmic preparations, as they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Moreover, since fenoldopam may cause a dose-dependent increase in intraocular pressure, caution should be exercised if it is administered to patients with glaucoma or ocular hypertension receiving ophthalmic beta-blockers.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48734/', '[1] "Product Information. Corlopam (fenoldopam)." Neurex, Menlo Park, CA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48664, 'Fingolimod', 'Timolol (ophthalmic)', 'Moderate', 'The risk of bradycardia and atrioventricular (AV) block may be increased during initiation of fingolimod treatment in patients receiving beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or AV conduction such as alectinib, atazanavir, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction.', NULL, 'Fingolimod has not been adequately studied in patients receiving other drugs that can slow the heart rate or AV conduction. Close monitoring is recommended during initiation of fingolimod treatment in these patients. The first dose should always be administered in a setting where resources to appropriately manage symptomatic bradycardia are available.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48735/', '[1] FDA. U.S. Food and Drug Administration "FDA Drug Safety Communication: Revised recommendations for cardiovascular monitoring and use of multiple sclerosis drug Gilenya (fingolimod). Available from: URL: http://www.fda.gov/Drugs/DrugSafety/ucm303192.htm#data." ([2012 May 14]):[2] "Product Information. Gilenya (fingolimod)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48665, 'Flecainide', 'Timolol (ophthalmic)', 'Moderate', 'Beta-blockers and flecainide may have additive negative inotropic effects. Areas under the curve were increased for both drugs and negative inotropic effects occurred when flecainide and propranolol were given to normal subjects. A case of bradycardia, atrioventricular block and cardiac arrest has been reported after sotalol was added to flecainide.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration.', NULL, 'Careful monitoring of the patient''s hemodynamic status is recommended during concomitant administration. The same precaution should be observed when beta blocker ophthalmic solutions are used, since they are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Patients should be advised to promptly report symptoms such as dizziness, slow or irregular heartbeats, syncope, or palpitations.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48736/', '[1] Lewis GP, Holtzman JL "Interaction of flecainide with digoxin and propranolol." Am J Cardiol 53 (1984): b52-7[2] Minish T, Herd A "Symptomatic bradycardia secondary to interaction between topical timolol maleate, verapamil, and flecainide: a case report." J Emerg Med 22 (2002): 247-9[3] Holtzman JL, Kvam DC, Berry DA, et al "The pharmacodynamic and pharmacokinetic interaction of flecainide acetate with propranolol: effects on cardiac function and drug clearance." Eur J Clin Pharmacol 33 (1987): 97-9[4] Myerburg RJ, Kessler KM, Cox MM, et al "Reversal of proarrhythmic effects of flecainide acetate and encainide hydrochloride by propranolol." Circulation 80 (1989): 1571-9[5] Warren R, Vohra J, Hunt D, Hamer A "Serious interactions of sotalol with amiodarone and flecainide." Med J Aust 152 (1990): 277', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48666, 'Fluoxetine', 'Timolol (ophthalmic)', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase the systemic effects of topically administered timolol, which is metabolized by the isoenzyme. Following ocular administration, timolol is systemically absorbed and can reach plasma levels associated with adverse beta-adrenergic blocking effects such as bronchospasm, depression, bradycardia, and hypotension. The risk may be increased if clearance of the drug is significantly diminished by concomitant CYP450 2D6 inhibitors.', NULL, 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors.', NULL, 'Patients should be monitored for systemic beta-adrenergic blocking effects of topical timolol during coadministration with CYP450 2D6 inhibitors such as cimetidine, quinidine, and certain selective serotonin reuptake inhibitors. Particular caution is warranted in elderly patients, since they are generally more susceptible to adverse effects of topically administered beta blockers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48737/', '[1] Fraunfelder FT, Fraunfelder FW; Randall JA "Drug-Induced Ocular Side Effects 5th" Boston, MA: Butterworth-Heinemann (2001):[2] Edeki TI, He HB, Wood AJJ "Pharmacogenetic explanation for excessive beta-blockade following timolol eye drops: potential for oral-ophthalmic drug interaction." JAMA 274 (1995): 1611-3[3] Lewis RV, Lennard MS, Jackson PR, Tucker GT, Ramsay LE, Wo