INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (48775, 'Tocainide', 'Timolol (ophthalmic)', 'Moderate', 'The concomitant use of tocainide and beta-blockers may have additive effects on cardiac index, left ventricular function, and pulmonary wedge pressure. While significant interactions have not been reported in studies, cases of asystole have been reported in patients with preexisting electrophysiologic abnormalities after tocainide and metoprolol were coadministered. The mechanism is not known, but may be related to an additive effect on the sinus node or conduction system, especially in patients with sick sinus syndrome. Cases of confusion and paranoia have also been reported after coadministration of tocainide and propranolol.', NULL, 'The concomitant use of tocainide and beta blockers should probably be avoided in patients with atrioventricular conduction abnormalities or sick sinus syndrome.', NULL, 'The concomitant use of tocainide and beta blockers should probably be avoided in patients with atrioventricular conduction abnormalities or sick sinus syndrome. This combination should be used cautiously in other patients, with careful dose titration and close clinical and electrocardiographic monitoring for efficacy and safety.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/48846/', '[1] Roden DM, Woosley RL "Tocainide." N Engl J Med 315 (1986): 41-5[2] Rubino M, Jackson E "Severe paranoia with concomitant tocainide and propranolol therapy." Clin Pharm 1 (1982): 177-9[3] Ikram H "Hemodynamic and electrophysiologic interactions between antiarrhythmic drugs and beta blockers, with special reference to tocainide." Am Heart J 100 (1