INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353094, 'Docetaxel', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 or dual CYP450 3A4 and P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of docetaxel, which is a substrate of both CYP450 3A4 and P-gp.', 'DDInter', 1767317475, 'Caution is advised if docetaxel is prescribed in combination with CYP450 3A4 inhibitors or dual CYP450 3A4 and P-gp inhibitors. Patients should be closely monitored for the development of docetaxel toxicity such as myelosuppression, stomatitis, neurotoxicity (e.g., paresthesia, dysesthesia, pain), myalgia, asthenia, fluid retention, nausea, vomiting, and diarrhea. Dosage reduction of docetaxel may be required if an interaction is suspected.', 'Metabolism', 'Caution is advised if docetaxel is prescribed in combination with CYP450 3A4 inhibitors or dual CYP450 3A4 and P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353095, 'Dofetilide', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353096, 'Donepezil', 'Aprepitant', 'Minor', 'Coadministration with inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations of donepezil, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353097, 'Doravirine', 'Aprepitant', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of doravirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353098, 'Doxorubicin', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of chemotherapeutic agents that are primarily metabolized by CYP450 3A4. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant. In general, the effect of aprepitant on the pharmacokinetics of CYP450 3A4 substrates is expected to be greater when the substrates are administered orally as opposed to intravenously and may be altered following prolonged administration.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials. The potential for increased systemic toxicities of these agents should be considered. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353099, 'Doxorubicin (liposomal)', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of chemotherapeutic agents that are primarily metabolized by CYP450 3A4. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant. In general, the effect of aprepitant on the pharmacokinetics of CYP450 3A4 substrates is expected to be greater when the substrates are administered orally as opposed to intravenously and may be altered following prolonged administration.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials. The potential for increased systemic toxicities of these agents should be considered. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353100, 'Dronabinol', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9. The mechanism is accelerated clearance due to induction of CYP450 2C9 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353101, 'Dronedarone', 'Aprepitant', 'Moderate', 'Coadministration of dronedarone with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of both drugs. Dronedarone itself is also a substrate and moderate inhibitor of CYP450 3A4. Theoretically, competitive and/or noncompetitive metabolic inhibition may occur. Because dronedarone is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Caution is advised if dronedarone is prescribed in combination with a drug that is an inhibitor of CYP450 3A4. Pharmacologic response to dronedarone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to seek medical attention if they experience symptoms that could indicate dronedarone toxicity such as excessive fatigue, fainting, difficulty breathing, chest pain or tightness, bradycardia, or symptoms of torsade de pointes such as dizziness, palpitations, irregular heartbeat, shortness of breath, and syncope. Likewise, clinical and/or laboratory monitoring may be appropriate for the coadministered drug following the addition or withdrawal of dronedarone, and the dosage adjusted as necessary.', 'Metabolism', 'Caution is advised if dronedarone is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353102, 'Drospirenone', 'Aprepitant', 'Moderate', 'RECOMMENDED: Aprepitant and its prodrug, fosaprepitant, may reduce the efficacy of low-dose hormonal contraceptives during and for up to 28 days after treatment. The proposed mechanism is aprepitant induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 1767317475, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant. Alternative or additional methods of birth control is recommended during treatment with aprepitant and for one month following the last dose of aprepitant. Data are not available for single-dosing of aprepitant 40 mg. However, since the timing of aprepitant administration relative to ovulation could cause contraceptive failure, the same precaution as for the 125 mg/80 mg regimen should be observed. Intrauterine systems are unlikely to be significantly affected because of their local action. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353103, 'Dutasteride', 'Aprepitant', 'Moderate', 'Based on in vitro data, coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dutasteride, which is metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353104, 'Duvelisib', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of duvelisib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of toxicities such as diarrhea, colitis, neutropenia, thrombocytopenia, pneumonitis, infections, cutaneous reactions and hepatotoxicity, and the dosing adjusted or discontinued in accordance with the duvelisib product labeling.', 'Metabolism', 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353105, 'Echinacea', 'Aprepitant', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 1767317475, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353106, 'Efavirenz', 'Aprepitant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St. John''s wort. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353107, 'Elagolix', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of the OATP 1B1 hepatic uptake transporter, CYP450 3A4 metabolic isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of elagolix. The effect of P-gp inhibitors on the pharmacokinetics of elagolix has not been studied, but some increases in elagolix exposure may occur, as it is a P-gp substrate. Increased exposure to elagolix may increase the risk of serious adverse effects such as bone loss, suicidal ideation and behavior, exacerbation of mood disorders, and hepatic transaminase elevations.', 'DDInter', 1767317475, 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors. Patients should be monitored for potentially altered effects of elagolix following the initiation or discontinuation of these inhibitors, and the elagolix dosage adjusted as necessary.', 'Metabolism', 'Caution is advised when elagolix is used with OATP 1B1, CYP450 3A4, and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353108, 'Eletriptan', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eletriptan, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics. Patients should have vital signs monitored regularly and advised to notify their physician if they experience signs and symptoms of vasospasm such as numbness, tingling, or cyanosis in the extremities; muscle pains; weakness; or chest pain or tightness. Alternatively, other 5-HT1 receptor agonists that are not metabolized by CYP450 3A4 may be considered, such as frovatriptan, naratriptan, rizatriptan, sumatriptan, and zolmitriptan.', 'Metabolism', 'Eletriptan should not be used within at least 72 hours of treatment with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, nefazodone, delavirdine, most protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353109, 'Eliglustat', 'Aprepitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317475, 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs). In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a moderate CYP450 3A4 inhibitor. However, eliglustat should not be used with a moderate CYP450 3A4 inhibitor in CYP450 2D6 IMs or poor metabolizers (PMs). Moderate CYP450 3A4 inhibitors include aprepitant, ciprofloxacin, clotrimazole, crizotinib, darunavir, diltiazem, dronedarone, fluconazole, fusidic acid, imatinib, isavuconazonium, miconazole, mifepristone, netupitant, quinupristin-dalfopristin, ranolazine, stiripentol, and verapamil. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, duloxetine, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propoxyphene, quinidine, ranolazine, sertraline, stiripentol, and terbinafine.', 'Metabolism', 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353110, 'Encorafenib', 'Aprepitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. Increased exposure to encorafenib may increase the risk of serious and life-threatening adverse effects such as hemorrhage, uveitis, QT prolongation, hepatotoxicity, dermatologic reactions, and new malignancies.', 'DDInter', 1767317475, 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-half of the dose used prior to addition of the moderate CYP450 3A4 inhibitor. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of encorafenib with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353111, 'Enfortumab vedotin', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of enfortumab vedotin. Enfortumab vedotin is an antibody-drug conjugate (ADC) that releases MMAE via proteolytic cleavage, and MMAE has been shown in vitro to be primarily metabolized by CYP450 3A4.', 'DDInter', 1767317475, 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be closely monitored for development or exacerbation of toxicities such as peripheral neuropathy, hyperglycaemia, dermatologic reactions (maculopapular rash, pruritus, symmetrical drug-related intertriginous and flexural exanthema (SDRIFE), bullous dermatitis, exfoliative dermatitis, palmar-plantar erythrodysesthesia), and ocular disorders (dry eyes, keratitis, blurred vision, limbal stem cell deficiency), and the dosing of enfortumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when enfortumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353112, 'Entrectinib', 'Aprepitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of entrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to entrectinib may increase the risk and/or severity of adverse effects such as cognitive impairment, mood disorders, dizziness, sleep disturbances, liver enzyme elevations, hyperuricemia, congestive heart failure, edema, myocarditis, QT prolongation, vision problems, anemia, and neutropenia.', 'DDInter', 1767317475, 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible. If coadministration is necessary, the dosage of entrectinib should be reduced to 100 mg once daily for adults and pediatrics patients (12 years and older) with a body surface area (BSA) greater than 1.5 m2. Following discontinuation, and after an appropriate washout period of the CYP450 3A4 inhibitor (i.e., 3 to 5 half-lives), the entrectinib dosage taken prior to initiating the CYP450 3A4 inhibitor may be resumed. For pediatric patients with a body surface area of 1.5 m2 or less, concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', 'Concomitant use of entrectinib with moderate CYP450 3A4 inhibitors should be avoided when possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353113, 'Enzalutamide', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 and/or 3A4 may increase the plasma concentrations of enzalutamide, which is primarily metabolized by CYP450 2C8 to its pharmacologically active metabolite, N-desmethyl enzalutamide, and to a lesser extent by CYP450 3A4.', 'DDInter', 1767317475, 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased side effects such as seizure, asthenia, fatigue, diarrhea, arthralgia, musculoskeletal pain, paresthesia, hot flushes, peripheral edema, headache, dizziness, insomnia, hematuria, anxiety, and hypertension.', 'Metabolism', 'Pharmacologic response to enzalutamide should be monitored more closely whenever a CYP450 2C8 and/or 3A4 inhibitor is added to or withdrawn from therapy, and the dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353114, 'Eplerenone', 'Aprepitant', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of eplerenone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'When used with moderate CYP450 3A4 inhibitors, the initial dosage of eplerenone should be reduced to 25 mg once daily for the treatment of hypertension. Patients already on eplerenone who start treatment with a moderate CYP450 3A4 inhibitor should have serum potassium and creatinine checked in 3 to 7 days.', 'Metabolism', 'When used with moderate CYP450 3A4 inhibitors, the initial dosage of eplerenone should be reduced to 25 mg once daily for the treatment of hypertension.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353115, 'Ergometrine', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353116, 'Ergotamine', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353117, 'Erythromycin', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353118, 'Escitalopram', 'Aprepitant', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme. Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353119, 'Eslicarbazepine', 'Aprepitant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St. John''s wort. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353120, 'Estazolam', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of benzodiazepines that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4. The potential for increased pharmacologic effects of the benzodiazepine, including central nervous system and respiratory depression, should be considered. The interaction is not expected to occur with lorazepam, oxazepam or temazepam, which are primarily metabolized via glucuronidation. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353121, 'Esterified estrogens', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
