INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353126, 'Eszopiclone', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of both zopiclone and its pharmacologically active S(-) enantiomer, eszopiclone. Zopiclone has been shown in vitro to be metabolized by CYP450 3A4 and CYP450 2C8, while eszopiclone is primarily metabolized by CYP450 3A4 and 2E1 via demethylation and oxidation.', 'DDInter', 1767317475, 'Caution is advised when zopiclone or eszopiclone is coadministered with moderate CYP450 3A4 inhibitors. A dosage reduction may be required if an interaction is suspected. Patients should be advised to avoid driving or operating hazardous machinery until they know how these medications affect them, preferably at least 12 hours after administration of the hypnotic.', 'Metabolism', 'Caution is advised when zopiclone or eszopiclone is coadministered with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353127, 'Ethinylestradiol', 'Aprepitant', 'Moderate', 'RECOMMENDED: Aprepitant and its prodrug, fosaprepitant, may reduce the efficacy of low-dose hormonal contraceptives during and for up to 28 days after treatment. The proposed mechanism is aprepitant induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 1767317475, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant. Alternative or additional methods of birth control is recommended during treatment with aprepitant and for one month following the last dose of aprepitant. Data are not available for single-dosing of aprepitant 40 mg. However, since the timing of aprepitant administration relative to ovulation could cause contraceptive failure, the same precaution as for the 125 mg/80 mg regimen should be observed. Intrauterine systems are unlikely to be significantly affected because of their local action. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with aprepitant or fosaprepitant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
