INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353130, 'Etoposide', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of chemotherapeutic agents that are primarily metabolized by CYP450 3A4. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant. In general, the effect of aprepitant on the pharmacokinetics of CYP450 3A4 substrates is expected to be greater when the substrates are administered orally as opposed to intravenously and may be altered following prolonged administration.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials. The potential for increased systemic toxicities of these agents should be considered. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353131, 'Etravirine', 'Aprepitant', 'Moderate', 'Coadministration of etravirine with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of etravirine and decreased plasma concentrations of the other drug. Etravirine itself is a substrate and inducer of CYP450 3A4. Theoretically, etravirine may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', 'DDInter', 1767317475, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4. In addition, dosage adjustments may be required for the coadministered drug if it is also a substrate of CYP450 3A4.', 'Metabolism', 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353132, 'Everolimus', 'Aprepitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycaemia, and hyperlipidemia may be increased.', 'DDInter', 1767317475, 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors. The manufacturer''s drug product labeling should be consulted for specific recommendations.', 'Metabolism, Absorption', 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
