INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353164, 'Isavuconazonium', 'Aprepitant', 'Moderate', 'Coadministration of isavuconazonium sulfate (prodrug of isavuconazole), a substrate as well as a moderate inhibitor of CYP450 3A4/5, with drugs that are both a substrate as well as inducer of CYP450 3A4/5 may result in decreased plasma concentrations of isavuconazole and increased plasma concentrations of the other drug. Isavuconazole may inhibit the metabolism of the coadministered drug while its own metabolism may be induced by the coadministered drug.', 'DDInter', 1767317475, 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are both a substrate and inducer of CYP450 3A4/5, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever these drugs are added to or withdrawn from combination therapy.', 'Metabolism', 'Caution is advised if isavuconazonium sulfate is used concomitantly with drugs that are both a substrate and inducer of CYP450 3A4/5, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353165, 'Isradipine', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when aprepitant or fosaprepitant is used concomitantly with drugs that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353166, 'Istradefylline', 'Aprepitant', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1.', 'DDInter', 1767317475, 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inhibitors. It is unknown if istradefylline will require a dosage adjustment if coadministered with a moderate CYP450 3A4 inhibitor. Clinical and laboratory monitoring of istradefylline should be considered whenever a moderate CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be monitored for adverse effects including dyskinesia, impulse control issues, dizziness, constipation, nausea, hallucinations, and insomnia.', 'Metabolism', 'Caution is advised if istradefylline is used with moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353167, 'Itraconazole', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353168, 'Ivabradine', 'Aprepitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of ivabradine with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, some authorities recommend initiating ivabradine at a dosage of 2.5 mg twice daily, provided the resting heart rate is at or above 70 beats per minute and heart rate is closely monitored.', 'Metabolism', 'Concomitant use of ivabradine with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353169, 'Ivacaftor', 'Aprepitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Please consult manufacturer''s product labeling for complete dosing information. For ivacaftor - The frequency of dosing should be reduced to 1 tablet or packet once a day when coadministered with moderate CYP450 3A4 inhibitors. Patients should continue to receive the same tablet or oral granule packet strength, but instead of dosing twice a day, the frequency should be reduced to once a day.', 'Metabolism', 'Please consult manufacturer''s product labeling for complete dosing information.', NULL, NULL, 'Major', 'pharmacodynamic');
