INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353171, 'Aprepitant', 'Ivosidenib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivosidenib, which is primarily metabolized by the isoenzyme. Increased exposure to ivosidenib may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death. Coadministration with ivosidenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4, including many of the known moderate inhibitors of the isoenzyme such as amprenavir, aprepitant, crizotinib, darunavir, diltiazem, dronedarone, erythromycin, imatinib, isavuconazonium, mifepristone, netupitant, and verapamil. Ivosidenib is an inducer of CYP450 3A4 and induces its own metabolism following multiple dosing.', 'DDInter', 1767317475, 'Concomitant use of ivosidenib with moderate CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided. If coadministration is required, close monitoring of electrocardiograms and electrolytes is recommended.', 'Metabolism', 'Concomitant use of ivosidenib with moderate CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
