INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353179, 'Aprepitant', 'Lesinurad', 'Moderate', 'Coadministration with inducers of CYP450 2C9 may decrease the plasma concentrations of lesinurad. According to the manufacturer, administration of lesinurad with the potent CYP450 inducer rifampin (600 mg daily) decreased lesinurad peak plasma concentration (Cmax) and systemic exposure (AUC) by approximately 25% and 40%, respectively, compared to administration of lesinurad alone.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of lesinurad should be considered during coadministration with CYP450 2C9 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of lesinurad should be considered during coadministration with CYP450 2C9 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
