INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353200, 'Methadone', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of methadone, which is metabolized primarily by CYP450 3A4, 2B6, 2C19, and to a lesser extent by CYP450 2C9 and 2D6. The possibility of prolonged and/or increased pharmacologic effects of methadone, such as central nervous system and respiratory depression, should be considered. In addition, high dosages (particularly above 200 mg/day) and serum levels of methadone have been associated with QT interval prolongation and torsade de pointes arrhythmia.', 'DDInter', 1767317475, 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to methadone should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of the CYP450 3A4 inhibitor in patients who are stabilized on their methadone regimen. Patients should be advised to report excessive drowsiness, nausea, or asthenia to their physician, and to seek immediate medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. ECG monitoring should be considered for patients on methadone with heart or liver disease; conduction abnormalities; electrolyte disturbances (i.e., hypokalemia, hypomagnesemia); concomitant use of drugs that may cause QT prolongation or electrolyte loss; concomitant use of CYP450 3A4 inhibitors; or use of methadone at dosages greater than 200 mg daily.', 'Metabolism', 'Caution is advised if methadone is prescribed in combination with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353201, 'Methylergometrine', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353202, 'Methylprednisolone', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of some corticosteroids. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Oral dexamethasone and methylprednisolone dosage should be reduced by approximately 50% when coadministered with the aprepitant 125 mg/80 mg regimen, and intravenous methylprednisolone dosage should be reduced by approximately 25%. The daily dosage of dexamethasone administered in clinical chemotherapy-induced nausea and vomiting studies with aprepitant was reduced to account for the drug interaction. No dosage adjustment for the corticosteroid is necessary during coadministration with aprepitant in a single 40 mg dose for prevention of postoperative nausea and vomiting. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended, as it has not been studied and the drug interaction profile may change during long-term use.', 'Metabolism', 'Oral dexamethasone and methylprednisolone dosage should be reduced by approximately 50% when coadministered with the aprepitant 125 mg/80 mg regimen, and intravenous methylprednisolone dosage should be reduced by approximately 25%.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353203, 'Methysergide', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ergot derivatives, which are primarily metabolized by the isoenzyme. The interaction has occurred in patients receiving ergotamine or dihydroergotamine with potent CYP450 3A4 inhibitors such as macrolide antibiotics and protease inhibitors.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors. Patients should be monitored for potential ergot toxicity including peripheral vasospasm, ischemia, thrombosis, tachycardia, and hypertension.', 'Metabolism', 'Caution is advised during concomitant use of ergot derivatives with mild or moderate CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353204, 'Miconazole', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353205, 'Midazolam', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of benzodiazepines that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4. The potential for increased pharmacologic effects of the benzodiazepine, including central nervous system and respiratory depression, should be considered. The interaction is not expected to occur with lorazepam, oxazepam or temazepam, which are primarily metabolized via glucuronidation. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353206, 'Midostaurin', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of midostaurin and its active metabolites, which are all substrates of the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors. Patients should be closely monitored for increased adverse reactions (e.g., nausea, vomiting, diarrhea, edema, hyperglycemia, hyperuricemia, QT prolongation, neutropenia, lymphopenia, thrombocytopenia, anemia), especially during the first week of consecutive midostaurin administration in patients with advanced systemic mastocytosis and during the first week of midostaurin administration in each cycle of chemotherapy in patients with acute myeloid leukemia.', 'Metabolism', 'Caution is advised when midostaurin is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353207, 'Mifepristone', 'Aprepitant', 'Moderate', 'Coadministration of mifepristone with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of both drugs. Mifepristone itself is also a substrate and moderate inhibitor of CYP450 3A4. Theoretically, competitive and/or noncompetitive metabolic inhibition may occur. Because mifepristone is associated with dose-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, caution is advised if mifepristone is prescribed in combination with a CYP450 3A4 inhibitor, particularly when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome. When the coadministered drug is also a CYP450 3A4 substrate, clinical and/or laboratory monitoring may be appropriate following the addition or withdrawal of mifepristone, and the dosage adjusted as necessary. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', 'Due to its potent antiglucocorticoid effects and potential to cause dose-related prolongation of the QT interval, caution is advised if mifepristone is prescribed in combination with a CYP450 3A4 inhibitor, particularly when used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353208, 'Mirabegron', 'Aprepitant', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of mirabegron, which has been shown in vitro to be a substrate of the isoenzyme. However, in vivo results indicate that CYP450 3A4 may play a limited role in the overall elimination. No dosage adjustment is recommended when mirabegron is administered in combination with ketoconazole and probably other CYP450 3A4 inhibitors.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353209, 'Mirtazapine', 'Aprepitant', 'Moderate', 'Coadministration of mirtazapine with drugs that inhibit one or more of its metabolic pathways may result in increased plasma concentrations of mirtazapine. In vitro data from human liver microsomes indicate that CYP450 2D6 and 1A2 are involved in the formation of the 8-hydroxy metabolite, while CYP450 3A4 is primarily responsible for the formation of the N-desmethyl and N-oxide metabolites.', 'DDInter', 1767317475, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4. Therapeutic response to mirtazapine should be monitored more closely following initiation, discontinuation, or dosing change of CYP450 inhibitors, and the mirtazapine dosage adjusted as necessary.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353210, 'Mitotane', 'Aprepitant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St. John''s wort. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353211, 'Modafinil', 'Aprepitant', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of modafinil (the racemate) and armodafinil (the R-enantiomer), which are both partially metabolized by the isoenzyme. Conversely, the plasma levels of some of these inhibitors may decrease, since many of them are also substrates of CYP450 3A4, and modafinil and armodafinil have been found to be modest inducers of CYP450 3A4. The clinical significance of this potential interaction is unknown. Clinical monitoring for altered effects of modafinil and armodafinil as well as the CYP450 3A4 inhibitor may be appropriate following addition or withdrawal of one or the other drug.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353212, 'Nafcillin', 'Aprepitant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St. John''s wort. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the dosage adjusted as necessary.', 'Metabolism', 'The possibility of diminished therapeutic response to aprepitant should be considered during coadministration with CYP450 3A4 inducers, particularly potent ones like carbamazepine, enzalutamide, mitotane, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353213, 'Naldemedine', 'Aprepitant', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', 'DDInter', 1767317475, 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp). Patients should be closely monitored for potential opioid withdrawal symptoms as well as other adverse effects of naldemedine.', 'Metabolism', 'Caution is advised during concomitant use of naldemedine with moderate inhibitors of CYP450 3A4 and/or moderate or potent inhibitors of P-glycoprotein (P-gp).', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353214, 'Naloxegol', 'Aprepitant', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of naloxegol, which is primarily metabolized by the isoenzyme. Increased exposure to naloxegol may precipitate opioid withdrawal symptoms such as hyperhidrosis, lacrimation, rhinorrhea, chills, diarrhea, abdominal pain, anxiety, insomnia, irritability, restlessness, and yawning.', 'DDInter', 1767317475, 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the dosage of naloxegol should be reduced to 12.5 mg once daily and the patient closely monitored for opioid withdrawal symptoms.', 'Metabolism', 'Concomitant use of naloxegol with moderate CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353215, 'Naproxen', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 2C9. The mechanism is accelerated clearance due to induction of CYP450 2C9 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever aprepitant or fosaprepitant is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant must be used concomitantly with medications that undergo metabolism by CYP450 2C9, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
