INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353324, 'Triazolam', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of benzodiazepines that are metabolized by CYP450 3A4. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant.', 'DDInter', 1767317475, 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4. The potential for increased pharmacologic effects of the benzodiazepine, including central nervous system and respiratory depression, should be considered. The interaction is not expected to occur with lorazepam, oxazepam or temazepam, which are primarily metabolized via glucuronidation. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', 'Caution is advised if aprepitant or fosaprepitant is administered with benzodiazepines that are metabolized by CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
