INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353344, 'Warfarin', 'Aprepitant', 'Moderate', 'Coadministration of aprepitant or its prodrug, fosaprepitant, during warfarin therapy may result in clinically significant decreases in the INR or prothrombin time. The likely mechanism is aprepitant induction of CYP450 2C9, the isoenzyme responsible for metabolic clearance of the biologically more active S(-) enantiomer of warfarin.', 'DDInter', 1767317475, 'In patients treated with warfarin, the INR should be closely monitored in the 2-week period, particularly at 7 to 10 days, following initiation of the 3-day regimen of aprepitant or fosaprepitant with each chemotherapy cycle or following a single 40 mg dose of aprepitant for the prevention of postoperative nausea and vomiting. The same precaution may be applicable during therapy with other oral anticoagulants, although clinical data are lacking.', 'Metabolism', 'In patients treated with warfarin, the INR should be closely monitored in the 2-week period, particularly at 7 to 10 days, following initiation of the 3-day regimen of aprepitant or fosaprepitant with each chemotherapy cycle or following a single 40 mg dose of aprepitant for the prevention of postoperative nausea and vomiting.', NULL, NULL, 'Moderate', 'pharmacodynamic');
