INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353390, 'Ivacaftor', 'Desogestrel', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these pathways due to inhibition by ivacaftor and its pharmacologically active M1 metabolite.', 'DDInter', 1767317475, 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used with drugs that are substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353391, 'Desogestrel', 'Lapatinib', 'Moderate', 'Coadministration with lapatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-glycoprotein (P-gp) efflux transporter. The mechanism is decreased clearance via these routes due to inhibition by lapatinib.', 'DDInter', 1767317475, 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lapatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if lapatinib must be used concurrently with medications that are substrates of the CYP450 2C8 isoenzyme, CYP450 3A4 isoenzyme, and/or P-gp efflux transporter, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353392, 'Desogestrel', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 3A4. In vitro and in vivo studies have shown that both estrogens and progestins are partially metabolized by CYP450 3A4. Studies of letermovir with midazolam, a CYP450 3A4 probe substrate, have shown the net effect of letermovir on the CYP450 3A4 isoenzyme is moderate inhibition.', 'DDInter', 1767317475, 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy. When choosing an oral contraceptive, consider choosing an ethinyl estradiol-levonorgestrel product as this combination has been studied with letermovir and no clinically significant interaction has been found.', 'Metabolism', 'Monitor estrogen/progestin therapy for altered pharmacologic response and adjust dosage(s) accordingly whenever letermovir is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
