INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1353422, 'Desogestrel', 'Rosuvastatin', 'Minor', 'Coadministration with rosuvastatin may increase the plasma concentrations of certain oral contraceptive hormones. The mechanism of interaction has not been established. The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive doses.', 'DDInter', 1767317475, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
