INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (99261, 'Oxycodone', 'Ozanimod', 'Major', 'Coadministration of narcotic analgesics with monoamine oxidase inhibitors (MAOIs) has been associated with rare reports of anxiety, confusion, hypotension, respiratory depression, cyanosis, and coma. The mechanism of interaction is unknown, but may involve potentiation of central nervous system (CNS) and respiratory depressant effects by MAOIs.', NULL, 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.', NULL, 'Narcotic analgesics should generally not be used with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with opioids.', 'Others', 'Acetaminophen, Ibuprofen, Celecoxib, Acetylsalicylic acid, Naloxone', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Abatacept, Siponimod, Apremilast, Inebilizumab, Cladribine, Belumosudil, Belimumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/99366/', '[1] "Product Information. Tylenol with Codeine (acetaminophen-codeine)." Janssen Pharmaceuticals, Raritan, NJ.[2] Zornberg GL, Hegarty JD "Adverse interaction between propoxyphene and phenelzine." Am J Psychiatry 150 (1993): 1270-1[3] "Product Information. Emsam (selegiline)." Bristol-Myers Squibb, Princeton, NJ.[4] Michaels I, Serrins M, Shier NQ, Barash PG "Anesthesia for cardiac surgery in patients receiving monoamine oxidase inhibitors." Anesth Analg 63 (1984): 1041-4[5] Evans-Prosser CD "The use of pethidine and morphin